import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [recor, setError] = useState('');

    useEffect(() => {
        fetchUtilisateurs();
    }, []);

    const fetchUtilisateurs = async () => {
        try {
            const response = await axios.get('http://localhost:3000/utilisateurs');
            setUtilisateurs(response.data.slice(0, 10)); // Limite à 10 utilisateurs
        } catch (err) {
            setError('Erreur lors de la récupération des utilisateurs');
        }
    };

    return (
        <div>
            <h1>Liste des Utilisateurs</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nom</th>
                        <th>Prénom</th>
                        <th>Email</th>
                        <th>Date d'inscription</th>
                    </tr>
                </thead>
                <tbody>
                    {utilisateurs.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.nom}</td>
                            <td>{user.prenom}</td>
                            <td>{user.email}</td>
                            <td>{new Date(user.date_inscription).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Utilisateurs;
