import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [email, setEmail] = useState('');
    const [motDePasse, setMotDePasse] = useState('');
    const [editingUser, setEditingUser] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUtilisateurs();
    }, []);

    const fetchUtilisateurs = async () => {
      try {
        const [results, metadata] = await sequelize.query("SELECT * FROM \"Utilisateurs\"");
        res.status(200).json(results);
    } catch (err) {
        res.status(500).json({ message: err.message });
    }
        try {
            const response = await axios.get('http://localhost:3000/Utilisateurs');
            setUtilisateurs(response.data);
        } catch (err) {
            setError('Erreur lors de la récupération des utilisateurs');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingUser) {
                await axios.put(`http://localhost:3000/utilisateurs/${editingUser.id}`, {
                    nom,
                    prenom,
                    email,
                    mot_de_passe: motDePasse
                });
            } else {
                await axios.post('http://localhost:3000/utilisateurs', {
                    nom,
                    prenom,
                    email,
                    mot_de_passe: motDePasse
                });
            }
            fetchUtilisateurs();
            setNom('');
            setPrenom('');
            setEmail('');
            setMotDePasse('');
            setEditingUser(null);
        } catch (err) {
            setError('Erreur lors de la sauvegarde de l\'utilisateur');
        }
    };

    const handleEdit = (user) => {
        setNom(user.nom);
        setPrenom(user.prenom);
        setEmail(user.email);
        setMotDePasse('');
        setEditingUser(user);
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3000/utilisateurs/${id}`);
            fetchUtilisateurs();
        } catch (err) {
            setError('Erreur lors de la suppression de l\'utilisateur');
        }
    };

    return (
        <div>
            <h1>Gestion des Utilisateurs</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nom</label>
                    <input
                        type="text"
                        value={nom}
                        onChange={(e) => setNom(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Prénom</label>
                    <input
                        type="text"
                        value={prenom}
                        onChange={(e) => setPrenom(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Mot de passe</label>
                    <input
                        type="password"
                        value={motDePasse}
                        onChange={(e) => setMotDePasse(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">{editingUser ? 'Mettre à jour' : 'Créer'}</button>
                {editingUser && (
                    <button type="button" onClick={() => setEditingUser(null)}>
                        Annuler
                    </button>
                )}
            </form>

            <h2>Liste des Utilisateurs</h2>
            <ul>
                {utilisateurs.map((user) => (
                    <li key={user.id}>
                        {user.nom} {user.prenom} ({user.email})
                        <button onClick={() => handleEdit(user)}>Modifier</button>
                        <button onClick={() => handleDelete(user.id)}>Supprimer</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Utilisateurs;
