import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Utilisateurs = () => {
  const [utilisateurs, setUtilisateurs] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [form, setForm] = useState({ nom: '', prénom: '', email: '', mot_de_passe: '' });
  const [isUpdate, setIsUpdate] = useState(false);
  const [loginForm, setLoginForm] = useState({ email: '', mot_de_passe: '' });
  const [currentUser, setCurrentUser] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    fetchUtilisateurs();
    fetchCurrentUser();
  }, []);

  const fetchUtilisateurs = async () => {
    try {
      const response = await axios.get('http://localhost:3000/utilisateurs');
      setUtilisateurs(response.data);
    } catch (error) {
      handleError(error, 'Erreur lors de la récupération des utilisateurs');
    }
  };

  const fetchCurrentUser = async () => {
    try {
      const response = await axios.get('http://localhost:3000/me');
      setCurrentUser(response.data);
    } catch (error) {
      handleError(error, 'Erreur lors de la récupération des informations de l\'utilisateur connecté');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleLoginInputChange = (e) => {
    const { name, value } = e.target;
    setLoginForm({ ...loginForm, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isUpdate) {
        await axios.put(`http://localhost:3000/utilisateurs/${selectedUser.id}`, form);
        setSuccessMessage('Utilisateur modifié avec succès');
      } else {
        await axios.post('http://localhost:3000/utilisateurs', form);
        setSuccessMessage('Nouvel utilisateur créé avec succès');
      }
      setForm({ nom: '', prénom: '', email: '', mot_de_passe: '' });
      setIsUpdate(false);
      setSelectedUser(null);
      fetchUtilisateurs();
    } catch (error) {
      handleError(error, 'Erreur lors de la soumission du formulaire');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/login', loginForm);
      fetchCurrentUser();
      setSuccessMessage('Connexion réussie');
    } catch (error) {
      handleError(error, 'Erreur lors de la connexion');
    }
  };

  const handleEdit = (user) => {
    setSelectedUser(user);
    setForm({ nom: user.nom, prénom: user.prénom, email: user.email, mot_de_passe: '' });
    setIsUpdate(true);
    setSuccessMessage('');
    setErrorMessage('');
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/utilisateurs/${id}`);
      setSuccessMessage('Utilisateur supprimé avec succès');
      fetchUtilisateurs();
    } catch (error) {
      handleError(error, 'Erreur lors de la suppression de l\'utilisateur');
    }
  };

  const handleError = (error, defaultMessage) => {
    const message = error.response?.data?.message || defaultMessage;
    setErrorMessage(message);
    console.error('Détails de l\'erreur:', error);
  };

  return (
    <div>
      <h2>Liste des utilisateurs</h2>
      {successMessage && <div className="alert alert-success">{successMessage}</div>}
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
      <ul>
        {utilisateurs.map(utilisateur => (
          <li key={utilisateur.id}>
            {utilisateur.nom} {utilisateur.prénom}
            <button onClick={() => handleEdit(utilisateur)}>Éditer</button>
            <button onClick={() => handleDelete(utilisateur.id)}>Supprimer</button>
          </li>
        ))}
      </ul>

      <h3>{isUpdate ? 'Modifier l\'utilisateur' : 'Ajouter un nouvel utilisateur'}</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="nom"
          placeholder="Nom"
          value={form.nom}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="prénom"
          placeholder="Prénom"
          value={form.prénom}
          onChange={handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleInputChange}
          required
        />
        <input
          type="password"
          name="mot_de_passe"
          placeholder="Mot de passe"
          value={form.mot_de_passe}
          onChange={handleInputChange}
          required
        />
        <button type="submit">{isUpdate ? 'Modifier' : 'Ajouter'}</button>
      </form>

      <h3>Connexion</h3>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={loginForm.email}
          onChange={handleLoginInputChange}
          required
        />
        <input
          type="password"
          name="mot_de_passe"
          placeholder="Mot de passe"
          value={loginForm.mot_de_passe}
          onChange={handleLoginInputChange}
          required
        />
        <button type="submit">Connexion</button>
      </form>

      {currentUser && (
        <div>
          <h3>Utilisateur connecté</h3>
          <p>{currentUser.nom} {currentUser.prénom}</p>
        </div>
      )}
    </div>
  );
};

export default Utilisateurs;
