import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Utilisateurs from './components/Utilisateurs/Utilisateurs';
import AjouterUtilisateur from './components/AjouterUtilisateur/AjouterUtilisateur';
import DeleteUtilisateur from './components/Utilisateurs/DeleteUtilisateur';
import UpdateUtilisateur from './components/Utilisateurs/UpdateUtilisateur';
import { UserProvider } from './components/UserContext';
import styled from 'styled-components';

const Home = styled.h1`
  text-align: center;
  margin-top: 20px;
`;

function App() {
  return (
    <UserProvider>
      <Router>
        <div>
          <Routes>
            <Route path="/utilisateurs" element={<Utilisateurs />} />
            <Route path="/ajouter-utilisateur" element={<AjouterUtilisateur />} />
            <Route path="/supprimer-utilisateur" element={<SupprimerUtilisateur />} />
            <Route path="/modifier-utilisateur" element={<ModifierUtilisateur />} />
            <Route path="/" element={<Home>Bienvenue à la bibliothèque en ligne!</Home>} />
          </Routes>
        </div>
      </Router>
    </UserProvider>
  );
}

export default App;
