import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

// Suppression de l'importation du contexte si non utilisé
// import { UserContext } from '../UserContext';

const Container = styled.div`
    padding: 20px;
`;

const Title = styled.h1`
    text-align: center;
    margin-bottom: 20px;
`;

const Button = styled.button`
    margin: 10px;
    padding: 10px 20px;
    background-color: #007bff;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;

    &:hover {
        background-color: #0056b3;
    }
`;

const Table = styled.table`
    width: 100%;
    border-collapse: collapse;
    margin: 20px 0;
`;

const Th = styled.th`
    border: 1px solid #ddd;
    padding: 8px;
    background-color: #f2f2f2;
    text-align: left;
`;

const Td = styled.td`
    border: 1px solid #ddd;
    padding: 8px;
`;

const Tr = styled.tr`
    &:nth-child(even) {
        background-color: #f9f9f9;
    }

    &:hover {
        background-color: #f1f1f1;
    }
`;

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [records, setRecords] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:3000/Utilisateurs')
            .then(res => {
                if (res.data.length > 0) {
                    setUtilisateurs(Object.keys(res.data[0]).slice(0, 4));
                }
                setRecords(res.data);
            })
            .catch(err => {
                console.error('Erreur lors de la récupération des utilisateurs', err);
            });
    }, []);

    return (
        <Container>
            <Title>Liste des Utilisateurs</Title>
            <Button onClick={() => navigate('/ajouter-utilisateur')}>Ajouter un utilisateur</Button>
            <Table>
                <thead>
                    <Tr>
                        {utilisateurs.map((c, i) => (
                            <Th key={i}>{c}</Th>
                        ))}
                        <Th>Action</Th>
                    </Tr>
                </thead>
                <tbody>
                    {records.map((d, i) => (
                        <Tr key={i}>
                            <Td>{d.id}</Td>
                            <Td>{d.nom}</Td>
                            <Td>{d.prenom}</Td>
                            <Td>{d.email}</Td>
                            <Td>
                                <Button onClick={() => navigate(`/supprimer-utilisateur/${d.id}`)}>Supprimer</Button>
                                <Button onClick={() => navigate(`/modifier-utilisateur/${d.id}`)}>Modifier</Button>
                            </Td>
                        </Tr>
                    ))}
                </tbody>
            </Table>
        </Container>
    );
};

export default Utilisateurs;
