import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [records, setRecords] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchUtilisateurs = async () => {
            try {
                const res = await axios.get('http://localhost:3000/Utilisateurs');
                if (res.data.length > 0) {
                    setUtilisateurs(Object.keys(res.data[0]));
                    setRecords(res.data);
                } else {
                    setError('No data found');
                }
            } catch (err) {
                setError('Error fetching data');
            }
        };

        fetchUtilisateurs();
    }, []);

    return (
        <div>
            <h1>Liste des Utilisateurs</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {records.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            {utilisateurs.map((c, i) => (
                                <th key={i}>{c}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((d, i) => (
                            <tr key={i}>
                                {utilisateurs.map((col, j) => (
                                    <td key={j}>{col === 'date_inscription' ? new Date(d[col]).toLocaleDateString() : d[col]}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No records to display</p>
            )}
        </div>
    );
};

export default Utilisateurs;
