import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../services/api';
import styled from 'styled-components';
import Footer from './mise_enPage/Footer';
import Header from './mise_enPage/Header';

// Styles du composant
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 50px;
`;

const Input = styled.input`
  margin: 10px;
  padding: 10px;
  width: 300px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px 20px;
  margin: 10px;
  cursor: pointer;
  background-color: #007bff;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
`;

const ErrorText = styled.p`
  color: red;
  font-size: 14px;
`;

// Composant de connexion
const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // État pour gérer les erreurs
  const navigate = useNavigate();

  const handleLogin = async () => {
    setError(''); // Réinitialiser l'erreur avant la nouvelle tentative
    try {
      const response = await loginUser({ email, password });
      localStorage.setItem('token', response.data.token);
      navigate('/profile'); // Redirection après la connexion
    } catch (error) {
      setError(error.response?.data?.error || 'Erreur inconnue'); // Affichage de l'erreur
    }
  };

  return (
    <Container>
      <Header />
      <h2>Login</h2>
      <Input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <Input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      {error && <ErrorText>{error}</ErrorText>}
      <Button onClick={handleLogin}>Login</Button>
      <Footer />
    </Container>
  );
};

export default Login;
