import React from 'react';

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Utilisateurs from './components/Utilisateurs/Utilisateurs';
import Livres from './components/Livres/Livres';/*
import Categories from './components/Categories';
import Emprunts from './components/Emprunts';
import Commentaires from './components/Commentaires';
import Evaluations from './components/Evaluations';*/

function App() {
  return (
    <Router>
      <div>
        <Switch>
          <Route path="/utilisateurs" component={Utilisateurs} />
          <Route path="/livres" component={Livres} />
          <Route path="/categories" component={Categories} />
          <Route path="/emprunts" component={Emprunts} />
          <Route path="/commentaires" component={Commentaires} />
          <Route path="/evaluations" component={Evaluations} />
          <Route path="/" component={Home} />
        </Switch>
      </div>
    </Router>
  );
}

const Home = () => <h1>Bienvenue à la bibliothèque en ligne!</h1>;

export default App;
